wwv_flow_api.create_plugin(
 p_id=>wwv_flow_api.id(161556939618477644685)
,p_plugin_type=>'REGION TYPE'
,p_name=>'APEX.BI.DASHBOARD.D3'
,p_display_name=>'APEX Material BI Dashboard'
,p_supported_ui_types=>'DESKTOP:JQM_SMARTPHONE'
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'/* helper function to search in list of strings */',
'FUNCTION IS_IN_STR_LIST (',
'    P_IN_STR_LIST    VARCHAR2,',
'    P_IN_SRC_LIST    VARCHAR2,',
'    P_IN_SEPERATOR   VARCHAR2 := '':''',
') RETURN BOOLEAN',
'    DETERMINISTIC',
'AS',
'    VR_RET NUMBER := 0;',
'BEGIN',
'    SELECT',
'        COUNT(*)',
'    INTO VR_RET',
'    FROM',
'        DUAL',
'    WHERE',
'        EXISTS (',
'            SELECT',
'                1',
'            FROM',
'                TABLE ( APEX_STRING.SPLIT(',
'                    P_IN_STR_LIST,',
'                    P_IN_SEPERATOR',
'                ) ) T1',
'                JOIN TABLE ( APEX_STRING.SPLIT(',
'                    P_IN_SRC_LIST,',
'                    P_IN_SEPERATOR',
'                ) ) T2',
'                ON LOWER(',
'                    T1.COLUMN_VALUE',
'                ) = LOWER(',
'                    T2.COLUMN_VALUE',
'                )',
'        );',
'',
'    IF VR_RET > 0 THEN',
'        RETURN TRUE;',
'    ELSE',
'        RETURN FALSE;',
'    END IF;',
'END;',
'',
'/* helper function to download a file */',
'PROCEDURE DOWNLOAD_FILE (',
'    P_IN_BLOB           BLOB,',
'    P_IN_MIME_TYPE      VARCHAR2,',
'    P_IN_FILE_NAME      VARCHAR2,',
'    P_IN_AJAX_REFERENCE VARCHAR2 := NULL',
') AS',
'    VR_BLOB BLOB := P_IN_BLOB;',
'BEGIN',
'    HTP.INIT;',
'    OWA_UTIL.MIME_HEADER(',
'        NVL(',
'            P_IN_MIME_TYPE,',
'            ''application/octet''',
'        ),',
'        FALSE,',
'        ''UTF-8''',
'    );',
'    HTP.P(''Content-length: '' || DBMS_LOB.GETLENGTH(P_IN_BLOB));',
'    HTP.P(''Content-Disposition: attachment; filename="''||APEX_ESCAPE.HTML_ATTRIBUTE(P_IN_FILE_NAME) ||''"'');',
'    IF P_IN_AJAX_REFERENCE IS NOT NULL THEN',
'        HTP.P(''ajax-reference: '' || P_IN_AJAX_REFERENCE);',
'    END IF;',
'    OWA_UTIL.HTTP_HEADER_CLOSE;',
'    WPG_DOCLOAD.DOWNLOAD_FILE(VR_BLOB);',
'EXCEPTION',
'    WHEN OTHERS THEN',
'        APEX_DEBUG.ERROR(''APEX Material BI Dashboard - Error while Execute File Download'');',
'        APEX_DEBUG.ERROR(SQLERRM);',
'        APEX_DEBUG.ERROR(DBMS_UTILITY.FORMAT_ERROR_BACKTRACE);',
'        RAISE;',
'END;',
'',
'/* helper function to get cursor from a sql stmt */',
'FUNCTION SQL_TO_SYS_REFCURSOR (',
'    P_IN_SQL_STATEMENT   CLOB,',
'    P_IN_BINDS           SYS.DBMS_SQL.VARCHAR2_TABLE',
') RETURN SYS_REFCURSOR AS',
'    VR_CURS         BINARY_INTEGER;',
'    VR_REF_CURSOR   SYS_REFCURSOR;',
'    VR_EXEC         BINARY_INTEGER;',
'/* TODO make size dynamic */',
'    VR_BINDS        VARCHAR(100);',
'BEGIN',
'    VR_CURS         := DBMS_SQL.OPEN_CURSOR;',
'    DBMS_SQL.PARSE(',
'        VR_CURS,',
'        P_IN_SQL_STATEMENT,',
'        DBMS_SQL.NATIVE',
'    );',
'    IF P_IN_BINDS.COUNT > 0 THEN',
'        FOR I IN 1..P_IN_BINDS.COUNT LOOP',
'    /* TODO find out how to prevent ltrim */',
'            VR_BINDS := LTRIM(',
'                P_IN_BINDS(I),',
'                '':''',
'            );',
'            DBMS_SQL.BIND_VARIABLE(',
'                VR_CURS,',
'                VR_BINDS,',
'                V(VR_BINDS)',
'            );',
'        END LOOP;',
'    END IF;',
'',
'    VR_EXEC         := DBMS_SQL.EXECUTE(VR_CURS);',
'    VR_REF_CURSOR   := DBMS_SQL.TO_REFCURSOR(VR_CURS);',
'    RETURN VR_REF_CURSOR;',
'EXCEPTION',
'    WHEN OTHERS THEN',
'        IF DBMS_SQL.IS_OPEN(VR_CURS) THEN',
'            DBMS_SQL.CLOSE_CURSOR(VR_CURS);',
'        END IF;',
'        RAISE;',
'END;',
'',
'/* ajax function to get data async */',
'FUNCTION F_AJAX (',
'    P_REGION   IN   APEX_PLUGIN.T_REGION,',
'    P_PLUGIN   IN   APEX_PLUGIN.T_PLUGIN',
') RETURN APEX_PLUGIN.T_REGION_AJAX_RESULT IS',
'',
'    VR_RESULT              APEX_PLUGIN.T_REGION_AJAX_RESULT;',
'    VR_CUR                 SYS_REFCURSOR;',
'    VR_BIND_NAMES          SYS.DBMS_SQL.VARCHAR2_TABLE;',
'    VR_FUNCTION_TYPE       VARCHAR2(100) := APEX_APPLICATION.G_X01;',
'    VR_ITEM_ID             VARCHAR2(4000) := APEX_APPLICATION.G_X02;',
'    VR_PK                  VARCHAR2(32767) := APEX_APPLICATION.G_X03;',
'    VR_AJAX_REF            VARCHAR2(32767) := APEX_APPLICATION.G_X04;',
'    VR_CLOB                CLOB := NULL;',
'    VR_FILE_NAME           VARCHAR2(200);',
'    VR_BLOB                BLOB;',
'    VR_MIME_TYPE           VARCHAR2(200);',
'    VR_CONF_SQL   CONSTANT CLOB := P_REGION.ATTRIBUTE_03;',
'    VR_ITEM_JSON           BLOB := EMPTY_BLOB();',
'    VR_TMP_BLOB            BLOB := EMPTY_BLOB();',
'    VR_TMP_STR             VARCHAR2(32767) := NULL;',
'    VR_JSON_START CONSTANT BLOB := UTL_RAW.CAST_TO_RAW(''{"items":['');',
'    VR_JSON_COMMA CONSTANT BLOB := UTL_RAW.CAST_TO_RAW('','');',
'    VR_JSON_END   CONSTANT BLOB := UTL_RAW.CAST_TO_RAW('']}'');',
'    VR_JSON_MIME  CONSTANT VARCHAR2(30) := ''application/json'';',
'    VR_JSON_NAME  CONSTANT VARCHAR2(4) := ''json'';',
'',
'BEGIN',
'    CASE',
'        WHEN VR_FUNCTION_TYPE = ''itemData'' THEN',
'            BEGIN',
'               IF VR_CONF_SQL IS NOT NULL THEN',
'                   EXECUTE IMMEDIATE VR_CONF_SQL USING IN VR_ITEM_ID, OUT VR_ITEM_JSON;',
'               ELSE ',
'                   OWA_UTIL.STATUS_LINE(',
'                    NSTATUS         => 404,',
'                    CREASON         => ''Not Found'',',
'                    BCLOSE_HEADER   => TRUE',
'                   );',
'               END IF;',
'               IF DBMS_LOB.GETLENGTH(VR_ITEM_JSON) > 0 THEN',
'                    DOWNLOAD_FILE(',
'                        P_IN_BLOB             => VR_ITEM_JSON,',
'                        P_IN_MIME_TYPE        => VR_JSON_MIME,',
'                        P_IN_FILE_NAME        => VR_JSON_NAME,',
'                        P_IN_AJAX_REFERENCE   => VR_AJAX_REF',
'                    );',
'               ELSE ',
'                   OWA_UTIL.STATUS_LINE(',
'                    NSTATUS         => 404,',
'                    CREASON         => ''Not Found'',',
'                    BCLOSE_HEADER   => TRUE',
'                   );',
'               END IF;',
'              ',
'            EXCEPTION',
'                WHEN OTHERS THEN',
'                    IF VR_CUR%ISOPEN THEN',
'                        CLOSE VR_CUR;',
'                    END IF;',
'                    APEX_DEBUG.ERROR(''APEX Material BI Dashboard - Error while execute ajax call.'');',
'                    APEX_DEBUG.ERROR(SQLERRM);',
'                    APEX_DEBUG.ERROR(DBMS_UTILITY.FORMAT_ERROR_BACKTRACE);',
'                    OWA_UTIL.STATUS_LINE(',
'                    NSTATUS         => 416,',
'                    CREASON         => ''Error occured'',',
'                    BCLOSE_HEADER   => TRUE',
'                   );',
'            END;',
'        WHEN VR_FUNCTION_TYPE = ''items'' THEN',
'            BEGIN',
'                /* undocumented function of APEX for get all bindings */',
'                VR_BIND_NAMES   := WWV_FLOW_UTILITIES.GET_BINDS(P_REGION.SOURCE);',
'                /* execute binding*/',
'                /* temporary lob is a dirty workaround because json_arrayagg destroys every order in subquery  see -- outcomment line below */',
'                DBMS_LOB.CREATETEMPORARY(LOB_LOC => VR_ITEM_JSON,',
'                                         CACHE   => TRUE,',
'                                         DUR     => DBMS_LOB.CALL);',
'                DBMS_LOB.APPEND(VR_ITEM_JSON, VR_JSON_START);',
'                VR_CUR := SQL_TO_SYS_REFCURSOR(',
'                    RTRIM(P_REGION.SOURCE,'';''),',
'                    --TO_CLOB(''SELECT JSON_OBJECT(''''items'''' VALUE JSON_ARRAYAGG( JSON_BLOB RETURNING BLOB) RETURNING BLOB ) FROM ( '') || RTRIM(P_REGION.SOURCE,'';'') || TO_CLOB('' )''),',
'                    VR_BIND_NAMES',
'                );',
'               /* create json */',
'               LOOP',
'                    FETCH VR_CUR INTO VR_TMP_BLOB;',
'                    EXIT WHEN VR_CUR%NOTFOUND;',
'                    IF VR_CUR%ROWCOUNT > 1 THEN',
'                        DBMS_LOB.APPEND(VR_ITEM_JSON, VR_JSON_COMMA);',
'                    END IF;',
'                    DBMS_LOB.APPEND(VR_ITEM_JSON, VR_TMP_BLOB);',
'                END LOOP;',
'                ',
'                DBMS_LOB.APPEND(VR_ITEM_JSON, VR_JSON_END);',
'                /* trigger donwload of json */',
'                DOWNLOAD_FILE(',
'                    P_IN_BLOB             => VR_ITEM_JSON,',
'                    P_IN_MIME_TYPE        => VR_JSON_MIME,',
'                    P_IN_FILE_NAME        => VR_JSON_NAME',
'                );',
'                DBMS_LOB.FREETEMPORARY(VR_ITEM_JSON);',
'',
'            EXCEPTION',
'                WHEN OTHERS THEN',
'                    IF VR_CUR%ISOPEN THEN',
'                        CLOSE VR_CUR;',
'                    END IF;',
'                    APEX_DEBUG.ERROR(''APEX Material BI Dashboard - Error while execute ajax call.'');',
'                    APEX_DEBUG.ERROR(''APEX Material BI Dashboard - Please check your SQL Statements. SQL Statements needs to return on ore more JSON Values with column name JSON_BLOB.'');',
'                    APEX_DEBUG.ERROR(SQLERRM);',
'                    APEX_DEBUG.ERROR(DBMS_UTILITY.FORMAT_ERROR_BACKTRACE);',
'                    RAISE;',
'            END;',
'        WHEN VR_FUNCTION_TYPE = ''noteUpload'' THEN',
'            BEGIN',
'                DBMS_LOB.CREATETEMPORARY(',
'                    VR_CLOB,',
'                    FALSE,',
'                    DBMS_LOB.SESSION',
'                );',
'                FOR I IN 1..APEX_APPLICATION.G_F01.COUNT LOOP',
'                    VR_TMP_STR := WWV_FLOW.G_F01(I);',
'                    IF LENGTH(VR_TMP_STR) > 0 THEN',
'                        DBMS_LOB.WRITEAPPEND(',
'                            VR_CLOB,',
'                            LENGTH(VR_TMP_STR),',
'                            VR_TMP_STR',
'                        );',
'                    END IF;',
'',
'                END LOOP;',
'                EXECUTE IMMEDIATE ( P_REGION.ATTRIBUTE_14 )',
'                    USING IN VR_ITEM_ID, VR_CLOB;',
'                DBMS_LOB.FREETEMPORARY(VR_CLOB);',
'            EXCEPTION',
'                WHEN OTHERS THEN',
'                    DBMS_LOB.FREETEMPORARY(VR_CLOB);',
'                    APEX_DEBUG.ERROR(''APEX Material BI Dashboard - Error while executing dynamic PL/SQL Block to save note.'');',
'                    APEX_DEBUG.ERROR(SQLERRM);',
'                    APEX_DEBUG.ERROR(DBMS_UTILITY.FORMAT_ERROR_BACKTRACE);',
'                    RAISE;',
'            END;',
'        WHEN VR_FUNCTION_TYPE = ''imageDownload'' THEN',
'             BEGIN',
'                EXECUTE IMMEDIATE ( P_REGION.ATTRIBUTE_07 )',
'                    USING IN VR_PK, IN VR_ITEM_ID, OUT VR_FILE_NAME, OUT VR_MIME_TYPE, OUT VR_BLOB;',
'            EXCEPTION',
'                WHEN OTHERS THEN',
'                    APEX_DEBUG.ERROR(''APEX Material BI Dashboard - Error while executing dynamic PL/SQL Block to get Blob Source for Image Download.'');',
'                    APEX_DEBUG.ERROR(SQLERRM);',
'                    APEX_DEBUG.ERROR(DBMS_UTILITY.FORMAT_ERROR_BACKTRACE);',
'                    RAISE;',
'            END;',
'',
'            IF VR_FILE_NAME IS NOT NULL THEN',
'                DOWNLOAD_FILE(',
'                    P_IN_BLOB        => VR_BLOB,',
'                    P_IN_FILE_NAME   => VR_FILE_NAME,',
'                    P_IN_MIME_TYPE   => VR_MIME_TYPE',
'                );',
'            END IF;',
'        ELSE',
'            APEX_DEBUG.ERROR(''APEX Material BI Dashboard - Function Type does not exists'');',
'    END CASE;',
'',
'    RETURN VR_RESULT;',
'END;',
'',
'/* render function of the plugin */',
'FUNCTION F_RENDER (',
'    P_REGION                IN                      APEX_PLUGIN.T_REGION,',
'    P_PLUGIN                IN                      APEX_PLUGIN.T_PLUGIN,',
'    P_IS_PRINTER_FRIENDLY   IN                      BOOLEAN',
') RETURN APEX_PLUGIN.T_REGION_RENDER_RESULT IS',
'',
'    VR_RESULT            APEX_PLUGIN.T_REGION_RENDER_RESULT;',
'    VR_ITEMS2SUBMIT      APEX_APPLICATION_PAGE_REGIONS.AJAX_ITEMS_TO_SUBMIT%TYPE := APEX_PLUGIN_UTIL.PAGE_ITEM_NAMES_TO_JQUERY( P_REGION.AJAX_ITEMS_TO_SUBMIT );',
'    VR_ITEMS2SUBMIT_IMG  APEX_APPLICATION_PAGE_REGIONS.AJAX_ITEMS_TO_SUBMIT%TYPE := APEX_PLUGIN_UTIL.PAGE_ITEM_NAMES_TO_JQUERY( P_REGION.ATTRIBUTE_11 );',
'    VR_REGION_ID_REFRESH P_REGION.STATIC_ID%TYPE := APEX_ESCAPE.HTML_ATTRIBUTE(P_REGION.STATIC_ID);',
'    VR_REGION_ID         P_REGION.STATIC_ID%TYPE := VR_REGION_ID_REFRESH || ''-c'';',
'BEGIN',
'    ',
'    APEX_CSS.ADD_FILE(',
'        P_NAME        => ''bida.pkgd.min'',',
'        P_DIRECTORY   => P_PLUGIN.FILE_PREFIX,',
'        P_VERSION     => NULL,',
'        P_KEY         => ''bidacsssrc''',
'    );',
'    ',
'    IF (IS_IN_STR_LIST(P_REGION.ATTRIBUTE_04,''charts:map:clock'')) THEN',
'        APEX_JAVASCRIPT.ADD_LIBRARY(',
'            P_NAME        => ''bida-ddd.pkgd.min'',',
'            P_DIRECTORY   => P_PLUGIN.FILE_PREFIX,',
'            P_VERSION     => NULL,',
'            P_KEY         => ''bidadddjssrc''',
'        );',
'    END IF;',
'    ',
'    IF (IS_IN_STR_LIST(P_REGION.ATTRIBUTE_04,''charts'')) THEN',
'        APEX_JAVASCRIPT.ADD_LIBRARY(',
'            P_NAME        => ''bida-chart.pkgd.min'',',
'            P_DIRECTORY   => P_PLUGIN.FILE_PREFIX,',
'            P_VERSION     => NULL,',
'            P_KEY         => ''bidachartsjssrc''',
'        );',
'    END IF;',
'    ',
'    IF (IS_IN_STR_LIST(P_REGION.ATTRIBUTE_04,''calendar'')) THEN',
'        APEX_JAVASCRIPT.ADD_LIBRARY(',
'            P_NAME        => ''bida-calendar.pkgd.min'',',
'            P_DIRECTORY   => P_PLUGIN.FILE_PREFIX,',
'            P_VERSION     => NULL,',
'            P_KEY         => ''bidacalendarjssrc''',
'        );',
'    END IF;',
'       ',
'    APEX_JAVASCRIPT.ADD_LIBRARY(',
'        P_NAME        => ''bida.pkgd.min'',',
'        P_DIRECTORY   => P_PLUGIN.FILE_PREFIX,',
'        P_VERSION     => NULL,',
'        P_KEY         => ''bidajssrc''',
'    );',
'    ',
'    HTP.P(''<div id="''|| VR_REGION_ID ||''" class="bida-container"></div>'');',
'    ',
'    APEX_JAVASCRIPT.ADD_ONLOAD_CODE(''apexBIDashBoard(apex, $).initialize('' ||',
'    APEX_JAVASCRIPT.ADD_VALUE( VR_REGION_ID, TRUE ) ||',
'    APEX_JAVASCRIPT.ADD_VALUE( VR_REGION_ID_REFRESH, TRUE ) ||',
'    APEX_JAVASCRIPT.ADD_VALUE( APEX_PLUGIN.GET_AJAX_IDENTIFIER, TRUE ) ||',
'    APEX_JAVASCRIPT.ADD_VALUE( P_REGION.NO_DATA_FOUND_MESSAGE, TRUE ) ||',
'    APEX_JAVASCRIPT.ADD_VALUE( P_REGION.ATTRIBUTE_13, TRUE ) ||',
'    APEX_JAVASCRIPT.ADD_VALUE( P_REGION.ATTRIBUTE_01, TRUE ) ||',
'    APEX_JAVASCRIPT.ADD_VALUE( P_REGION.ATTRIBUTE_02, TRUE ) ||',
'    APEX_JAVASCRIPT.ADD_VALUE( P_REGION.ATTRIBUTE_04, TRUE ) ||',
'    APEX_JAVASCRIPT.ADD_VALUE( P_REGION.ATTRIBUTE_05, TRUE ) ||',
'    APEX_JAVASCRIPT.ADD_VALUE( VR_ITEMS2SUBMIT, TRUE ) ||',
'    APEX_JAVASCRIPT.ADD_VALUE( VR_ITEMS2SUBMIT_IMG, TRUE ) ||',
'    APEX_JAVASCRIPT.ADD_VALUE( P_REGION.ESCAPE_OUTPUT, TRUE ) ||',
'    APEX_JAVASCRIPT.ADD_VALUE( P_REGION.ATTRIBUTE_06, TRUE ) ||',
'    APEX_JAVASCRIPT.ADD_VALUE( P_PLUGIN.FILE_PREFIX || ''map/'', TRUE ) ||',
'    APEX_JAVASCRIPT.ADD_VALUE( P_REGION.ATTRIBUTE_08, TRUE ) ||',
'    APEX_JAVASCRIPT.ADD_VALUE( P_REGION.ATTRIBUTE_09, TRUE ) ||',
'    APEX_JAVASCRIPT.ADD_VALUE( P_REGION.ATTRIBUTE_10, TRUE ) ||',
'    APEX_JAVASCRIPT.ADD_VALUE( P_REGION.ATTRIBUTE_12, FALSE ) ||',
'    '');'');',
'',
'    RETURN VR_RESULT;',
'END;'))
,p_api_version=>1
,p_render_function=>'F_RENDER'
,p_ajax_function=>'F_AJAX'
,p_standard_attributes=>'SOURCE_SQL:AJAX_ITEMS_TO_SUBMIT:NO_DATA_FOUND_MESSAGE:ESCAPE_OUTPUT'
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_help_text=>'<p>This plug-in allows to integrate a freely configurable dashboard into Oracle APEX.</p><p>It offers many display options, e.g. charts, lists, KPI cards, world map, tables, HTML, analog clock, calendar and much more. Each dashboard item can be custo'
||'mized, even at runtime without page submit!</p><p>The main advantage of the dashboard is that end-users of your APEX app can configure one or more dashboards at runtime without having to touch the region or page by a developer! This was not possible '
||'in APEX so far! Of course, the dashboard can also be used as a fixed region.</p><p>The plug-in requires an APEX 5.1.3 or newer and an Oracle 12c or newer. The Sample App requires an APEX 20.2 or newer. However, you can quickly install it in a free wo'
||'rkspace on apex.oracle.com and then get started right away with the help of the Sample App!</p><p>The item dialog in the Sample App is just a very simplified example, of course in your application the respective data source (table, view, function...)'
||' and many setting options for the respective dashboard item can be offered there.</p><p><b>The Plug-in is published under CC BY - ND 4.0, so you can use it everywhere even in commercial projects but it ''s not allowed to modify the code and to publish'
||' the modified plug-in!</b></p>'
,p_version_identifier=>'1.0.0.12'
,p_about_url=>'https://github.com/RonnyWeiss/APEX-BI-Dashboard'
,p_files_version=>5014
);
